package com.example.jobportal.model;
import java.util.Date;
public class Job { private long id; private long employerId; private String title; private String location; private String type; private Integer salary; private String description; private Date postedOn; public long getId(){return id;} public void setId(long id){this.id=id;} public long getEmployerId(){return employerId;} public void setEmployerId(long employerId){this.employerId=employerId;} public String getTitle(){return title;} public void setTitle(String title){this.title=title;} public String getLocation(){return location;} public void setLocation(String location){this.location=location;} public String getType(){return type;} public void setType(String type){this.type=type;} public Integer getSalary(){return salary;} public void setSalary(Integer salary){this.salary=salary;} public String getDescription(){return description;} public void setDescription(String description){this.description=description;} public Date getPostedOn(){return postedOn;} public void setPostedOn(Date postedOn){this.postedOn=postedOn;} }